import React, { useEffect, useState } from 'react';
import axios from 'axios';
import EquipmentCard from './EquipmentCard';

const EquipmentList = () => {
    const [equipments, setEquipments] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/equipments/')
            .then(response => {
                setEquipments(response.data);
            })
            .catch(error => {
                console.error('Error fetching equipment data!', error);
                setError('Error fetching equipment data!');
            });
    }, []);

    const handleDelete = (id) => {
        axios.delete(`http://127.0.0.1:8000/equipments/${id}`)
            .then(response => {
                // После удаления фильтруем список и удаляем объект
                setEquipments(equipments.filter(equipment => equipment.id !== id));
            })
            .catch(error => {
                console.error('Error deleting equipment', error);
                setError('Error deleting equipment');
            });
    };

    return (
        <div>
            <h2>Equipment List</h2>
            {error && <p>{error}</p>}
            <div className="equipment-list">
                {equipments.map(equipment => (
                    <EquipmentCard key={equipment.id} equipment={equipment} onDelete={handleDelete} />
                ))}
            </div>
        </div>
    );
};

export default EquipmentList;
