import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Timeline.css';

const Timeline = () => {
  const [equipments, setEquipments] = useState([]);
  const [timeSlots, setTimeSlots] = useState([]);

  useEffect(() => {
    // Загружаем оборудование
    axios.get('http://127.0.0.1:8000/equipments/')
      .then((response) => {
        setEquipments(response.data);
      })
      .catch((error) => {
        console.error('Error fetching equipment:', error);
      });

    // Загружаем временные слоты
    axios.get('http://127.0.0.1:8000/time-slots/')
      .then((response) => {
        setTimeSlots(response.data);
      })
      .catch((error) => {
        console.error('Error fetching time slots:', error);
      });
  }, []);

  // Форматируем время в HH:MM
  const formatTime = (time) => {
    if (time && time.includes("T")) {
      const timePart = time.split('T')[1]; // Берем только время из формата "2025-03-01T10:00:00"
      return timePart.slice(0, 5); // Возвращаем только часы и минуты
    }
    return "00:00"; // Возвращаем дефолтное время, если его нет
  };

  return (
    <div className="timeline">
      <table>
        <thead>
          <tr>
            {/* Столбцы для каждого часа с 08:00 до 23:00 */}
            {Array.from({ length: 16 }, (_, i) => (
              <th key={i}>{`${8 + i}:00`}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {/* Проходим по каждому оборудованию и отображаем его временные слоты */}
          {equipments.map((equipment, index) => (
            <tr key={index}>
              <td>{equipment.name}</td>
              <td>{equipment.description}</td>
              {/* Отображение временных слотов для каждого оборудования */}
              {timeSlots.map((slot, idx) => (
                <td key={idx}>
                  {slot.equipment_id === equipment.id ? `${formatTime(slot.start_time)} - ${formatTime(slot.end_time)}` : ""}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Timeline;
