import React, { useEffect, useState } from "react";
import axios from "axios";
import './EquipmentList.css';


const EquipmentList = () => {
    const [equipments, setEquipments] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Получение всех данных о оборудовании
        axios.get("http://127.0.0.1:8000/equipments/")
            .then((response) => {
                setEquipments(response.data);
            })
            .catch((error) => {
                setError('Error fetching equipment data!');
            });
    }, []);

    const handleDelete = (id) => {
        // Удаление оборудования
        axios.delete(`http://127.0.0.1:8000/equipments/${id}`)
            .then(() => {
                setEquipments(equipments.filter(equipment => equipment.id !== id));
            })
            .catch((error) => {
                setError('Error deleting equipment');
            });
    };

    return (
        <div>
            {error && <p>{error}</p>}
            <div className="equipment-list">
                {equipments.map((equipment) => (
                    <div key={equipment.id} className="equipment-card">
                        <h3>{equipment.name}</h3>
                        <p><strong>Description:</strong> {equipment.description}</p>
                        <p><strong>Availability Start:</strong> {equipment.availability_start}</p>
                        <p><strong>Availability End:</strong> {equipment.availability_end}</p>
                        <p><strong>Created by:</strong> {equipment.created_by_user}</p>
                        <p><strong>Equipment ID:</strong> {equipment.id}</p>
                        {/* Можно добавить больше полей, если они есть */}
                        <button onClick={() => handleDelete(equipment.id)}>Delete</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default EquipmentList;
