import React, { useState, useEffect } from 'react';
import axios from 'axios';
im

const EquipmentList = () => {
  const [equipments, setEquipments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/equipments/")
      .then((response) => {
        setEquipments(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError("There was an error fetching the equipment data!");
        setLoading(false);
      });
  }, []);

  const handleDelete = (id) => {
    axios
      .delete(`http://127.0.0.1:8000/equipments/${id}/`)
      .then(() => {
        setEquipments(equipments.filter((equipment) => equipment.id !== id));
      })
      .catch((error) => {
        console.error("Error deleting equipment:", error);
      });
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="equipment-list">
      {equipments.map((equipment) => (
        <div key={equipment.id} className="equipment-card">
          <h3>{equipment.name}</h3>
          <p>{equipment.description}</p>
          <p>Available from: {equipment.availability_start}</p>
          <p>Available until: {equipment.availability_end}</p>
          <div className="bookings">
            {equipment.bookings && equipment.bookings.length > 0 ? (
              <div>
                <h4>Bookings:</h4>
                <ul>
                  {equipment.bookings.map((booking) => (
                    <li key={booking.start_time}>
                      <p>
                        {booking.is_active ? "Active" : "Inactive"}:{" "}
                        {booking.start_time} - {booking.end_time}
                      </p>
                    </li>
                  ))}
                </ul>
              </div>
            ) : (
              <p>No bookings</p>
            )}
          </div>
          <button onClick={() => handleDelete(equipment.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default EquipmentList;
