import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Timeline.css';

const Timeline = () => {
  const [equipments, setEquipments] = useState([]);
  const [timeSlots, setTimeSlots] = useState([]);

  // Загружаем данные о оборудовании и временных слотах
  useEffect(() => {
    // Загружаем оборудование
    axios.get('http://127.0.0.1:8000/equipments/')
      .then((response) => {
        setEquipments(response.data);
      })
      .catch((error) => {
        console.error('Error fetching equipment:', error);
      });

    // Загружаем временные слоты
    axios.get('http://127.0.0.1:8000/time-slots/')
      .then((response) => {
        setTimeSlots(response.data);
      })
      .catch((error) => {
        console.error('Error fetching time slots:', error);
      });
  }, []);

  // Форматируем время в HH:MM
  const formatTime = (time) => {
    if (!time) return "00:00"; 
    const [hours, minutes] = time.split(":");
    return `${hours}:${minutes}`;
  };

  // Рассчитываем позицию и высоту для временного слота
  const calculatePosition = (startTime, endTime) => {
    const start = startTime.split(":").map(Number);
    const end = endTime.split(":").map(Number);

    const startTotalMinutes = start[0] * 60 + start[1]; // Переводим в минуты с 08:00
    const endTotalMinutes = end[0] * 60 + end[1];

    const top = (startTotalMinutes - 480) * 2; // 480 — это 08:00 утра
    const height = (endTotalMinutes - startTotalMinutes) * 2; // 2 пикселя на минуту

    return { top, height };
  };

  return (
    <div className="timeline">
      <div className="timeline-header">
        {/* Столбцы для каждого часа с 08:00 до 23:00 */}
        {Array.from({ length: 16 }, (_, i) => (
          <div key={i} className="timeline-hour">{`${8 + i}:00`}</div>
        ))}
      </div>

      <div className="timeline-body">
        {/* Отображаем временные слоты для каждого оборудования */}
        {timeSlots.map((slot, index) => {
          const equipment = equipments.find(e => e.id === slot.equipment_id);

          // Вычисляем позицию и высоту карточки
          const { top, height } = calculatePosition(formatTime(slot.start_time), formatTime(slot.end_time));

          return (
            <div key={index} className="timeline-slot" style={{ top: `${top}px`, height: `${height}px` }}>
              <div className="timeline-slot-info">
                <strong>{equipment ? equipment.name : 'Loading...'}</strong>
                <p>{equipment ? equipment.description : 'Loading...'}</p>
                <p>{formatTime(slot.start_time)} - {formatTime(slot.end_time)}</p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Timeline;
