import React, { useState } from "react";
import axios from "axios";

function EquipmentForm() {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [availabilityStart, setAvailabilityStart] = useState("");
  const [availabilityEnd, setAvailabilityEnd] = useState("");
  const [slots, setSlots] = useState([]);

  const handleAddSlot = () => {
    if (availabilityStart && availabilityEnd) {
      setSlots([
        ...slots,
        { start_time: availabilityStart, end_time: availabilityEnd }
      ]);
      setAvailabilityStart("");
      setAvailabilityEnd("");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newEquipment = {
      name,
      description,
    };

    // Отправляем запрос на создание оборудования и временных слотов
    axios
      .post("http://127.0.0.1:8000/equipments_with_slots/", {
        equipment_data: newEquipment,
        slots: slots
      })
      .then((response) => {
        alert("Equipment and time slots added successfully!");
      })
      .catch((error) => {
        console.error("There was an error adding the equipment and slots!", error);
      });
  };

  return (
    <div>
      <h1>Add Equipment</h1>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <br />
        <label>Description:</label>
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <br />
        <label>Availability Start:</label>
        <input
          type="time"
          value={availabilityStart}
          onChange={(e) => setAvailabilityStart(e.target.value)}
          required
        />
        <br />
        <label>Availability End:</label>
        <input
          type="time"
          value={availabilityEnd}
          onChange={(e) => setAvailabilityEnd(e.target.value)}
          required
        />
        <br />
        <button type="button" onClick={handleAddSlot}>Add Slot</button>
        <br />
        <label>Added Slots:</label>
        <ul>
          {slots.map((slot, index) => (
            <li key={index}>{slot.start_time} - {slot.end_time}</li>
          ))}
        </ul>
        <button type="submit">Add Equipment</button>
      </form>
    </div>
  );
}

export default EquipmentForm;
