import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Timeline.css';

const Timeline = () => {
  const [bookings, setBookings] = useState([]);
  const [equipments, setEquipments] = useState([]);

  useEffect(() => {
    // Загружаем бронирования
    axios.get('http://127.0.0.1:8000/bookings/')
      .then((response) => {
        setBookings(response.data);
      })
      .catch((error) => {
        console.error('Error fetching bookings:', error);
      });

    // Загружаем оборудование
    axios.get('http://127.0.0.1:8000/equipments/')
      .then((response) => {
        setEquipments(response.data);
      })
      .catch((error) => {
        console.error('Error fetching equipment:', error);
      });
  }, []);

  // Форматируем время в HH:MM с проверкой на undefined
  const formatTime = (time) => {
    if (time && typeof time === 'string' && time.includes(":")) {
      const [hours, minutes] = time.split(":");
      return `${hours}:${minutes}`;
    }
    return "00:00"; // Возвращаем дефолтное время, если time не определено или не в правильном формате
  };

  // Функция для расчета позиции и высоты карточки бронирования
  const calculatePosition = (startTime, endTime) => {
    const [startHour, startMinute] = startTime.split(':').map(Number); // Забираем часы и минуты
    const [endHour, endMinute] = endTime.split(':').map(Number); // Забираем часы и минуты

    // Переводим время в минуты с начала дня
    const startTotalMinutes = (startHour - 8) * 60 + startMinute; // Часы с 8:00 утра
    const endTotalMinutes = (endHour - 8) * 60 + endMinute;

    return {
      top: startTotalMinutes * 2, // Умножаем на 2, чтобы 1 минута была 2 пикселя
      height: (endTotalMinutes - startTotalMinutes) * 2, // Высота = длительность * 2 пикселя
    };
  };

  return (
    <div className="timeline">
      <div className="timeline-header">
        {/* Столбцы для каждого часа с 08:00 до 23:00 */}
        {Array.from({ length: 16 }, (_, i) => (
          <div key={i} className="timeline-hour">{`${8 + i}:00`}</div>
        ))}
      </div>
      <div className="timeline-body">
        {/* Отображаем карточки для каждого бронирования */}
        {bookings.map((booking, index) => {
          const equipment = equipments.find(e => e.id === booking.equipment_id);

          // Проверка наличия start_time и end_time
          const startTime = booking.start_time ? formatTime(booking.start_time.split('T')[1].slice(0, 5)) : "00:00";
          const endTime = booking.end_time ? formatTime(booking.end_time.split('T')[1].slice(0, 5)) : "00:00";

          // Вычисляем позицию и высоту для карточки
          const { top, height } = calculatePosition(startTime, endTime);

          return (
            <div key={index} className="timeline-slot" style={{ top: `${top}px`, height: `${height}px` }}>
              <div className="timeline-slot-info">
                <strong>{equipment ? equipment.name : 'Loading...'}</strong>
                <p>{equipment ? equipment.description : 'Loading...'}</p>
                <p>{startTime} - {endTime}</p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Timeline;
