import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Timeline.css';

const Timeline = () => {
  const [bookings, setBookings] = useState([]);
  const [equipments, setEquipments] = useState([]);
  const [timeSlots, setTimeSlots] = useState([]);

  useEffect(() => {
    // Загружаем бронирования
    axios.get('http://127.0.0.1:8000/bookings/')
      .then((response) => {
        setBookings(response.data);
      })
      .catch((error) => {
        console.error('Error fetching bookings:', error);
      });

    // Загружаем оборудование
    axios.get('http://127.0.0.1:8000/equipments/')
      .then((response) => {
        setEquipments(response.data);
      })
      .catch((error) => {
        console.error('Error fetching equipment:', error);
      });

    // Загружаем временные слоты
    axios.get('http://127.0.0.1:8000/time-slots/')
      .then((response) => {
        setTimeSlots(response.data);
      })
      .catch((error) => {
        console.error('Error fetching time slots:', error);
      });
  }, []);

  // Форматируем время в HH:MM
  const formatTime = (time) => {
    if (time && time.includes("T")) {
      const timePart = time.split('T')[1]; // Берем только время из формата "2025-03-01T10:00:00"
      return timePart.slice(0, 5); // Возвращаем только часы и минуты
    }
    return "00:00"; // Возвращаем дефолтное время, если его нет
  };

  // Функция для расчета строки в таблице
  const calculateRow = (startTime, endTime) => {
    const [startHour, startMinute] = startTime.split(':').map(Number);
    const [endHour, endMinute] = endTime.split(':').map(Number);

    const startTotalMinutes = startHour * 60 + startMinute;
    const endTotalMinutes = endHour * 60 + endMinute;

    return [startTotalMinutes, endTotalMinutes];
  };

  return (
    <div className="timeline">
      <table>
        <thead>
          <tr>
            {/* Столбцы для каждого часа с 08:00 до 23:00 */}
            {Array.from({ length: 16 }, (_, i) => (
              <th key={i}>{`${8 + i}:00`}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {/* Проходим по каждому бронированию */}
          {bookings.map((booking, index) => {
            const equipment = equipments.find(e => e.id === booking.equipment_id);
            const timeSlot = timeSlots.find(ts => ts.id === booking.time_slot_id); // Получаем временной слот по time_slot_id

            if (!timeSlot) {
              return null; // Если нет временного слота, пропускаем бронирование
            }

            const startTime = formatTime(timeSlot.start_time);
            const endTime = formatTime(timeSlot.end_time);

            const [startTotalMinutes, endTotalMinutes] = calculateRow(startTime, endTime);

            const startHour = Math.floor(startTotalMinutes / 60);
            const startMinute = startTotalMinutes % 60;

            const endHour = Math.floor(endTotalMinutes / 60);
            const endMinute = endTotalMinutes % 60;

            const duration = endTotalMinutes - startTotalMinutes;
            const colSpan = Math.ceil(duration / 60);

            return (
              <tr key={index}>
                {/* Пустые ячейки до начала бронирования */}
                {Array.from({ length: startHour - 8 }).map((_, idx) => <td key={idx}></td>)}

                {/* Ячейки для бронирования */}
                <td colSpan={colSpan} className="booking-cell">
                  <div className="booking-info">
                    <strong>{equipment ? equipment.name : 'Loading...'}</strong>
                    <p>{equipment ? equipment.description : 'Loading...'}</p>
                    <p>{startTime} - {endTime}</p>
                  </div>
                </td>

                {/* Пустые ячейки после окончания бронирования */}
                {Array.from({ length: 23 - endHour }).map((_, idx) => <td key={idx}></td>)}

                {/* Добавляем пустую ячейку для столбца 23:00 */}
                <td></td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default Timeline;
