import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Timeline.css"; // Подключаем стили для таймлайна

function Timeline() {
  const [timeSlots, setTimeSlots] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [equipments, setEquipments] = useState([]);

  useEffect(() => {
    // Загружаем данные о временных слотах с сервера
    axios
      .get("http://127.0.0.1:8000/time-slots/")
      .then((response) => {
        setTimeSlots(response.data);
      })
      .catch((error) => {
        console.error("Error fetching time slots:", error);
      });

    // Загружаем данные о бронированиях с сервера
    axios
      .get("http://127.0.0.1:8000/bookings/")
      .then((response) => {
        setBookings(response.data);
      })
      .catch((error) => {
        console.error("Error fetching bookings:", error);
      });

    // Загружаем данные о оборудовании с сервера
    axios
      .get("http://127.0.0.1:8000/equipments/")
      .then((response) => {
        setEquipments(response.data);
      })
      .catch((error) => {
        console.error("Error fetching equipment:", error);
      });
  }, []);

  return (
    <div className="timeline">
      <h1>Таймлайн</h1>
      <div className="timeline-container">
        {/* Проверяем, есть ли временные слоты и отображаем их */}
        {timeSlots.length > 0 ? (
          timeSlots.map((slot) => {
            const equipment = equipments.find((e) => e.id === slot.equipment_id);
            const slotBookings = bookings.filter((b) => b.time_slot_id === slot.id);

            return (
              <div key={slot.id} className="time-slot">
                <div className="time-slot-header">
                  {equipment ? equipment.name : "Loading..."}
                  <br />
                  {`${slot.start_time} - ${slot.end_time}`}
                </div>
                <div className="bookings">
                  {slotBookings.length > 0 ? (
                    slotBookings.map((booking) => (
                      <div key={booking.id} className="booking">
                        <span>{booking.user_name}</span>
                      </div>
                    ))
                  ) : (
                    <div className="booking">Нет бронирований</div>
                  )}
                </div>
              </div>
            );
          })
        ) : (
          <div>Нет доступных временных слотов</div>
        )}
      </div>
    </div>
  );
}

export default Timeline;
