import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Timeline.css';

const Timeline = () => {
  const [bookings, setBookings] = useState([]);
  const [equipments, setEquipments] = useState([]);

  useEffect(() => {
    // Загружаем бронирования
    axios.get('http://127.0.0.1:8000/bookings/')
      .then((response) => {
        setBookings(response.data);
      })
      .catch((error) => {
        console.error('Error fetching bookings:', error);
      });

    // Загружаем оборудование
    axios.get('http://127.0.0.1:8000/equipments/')
      .then((response) => {
        setEquipments(response.data);
      })
      .catch((error) => {
        console.error('Error fetching equipment:', error);
      });
  }, []);

  const formatTime = (time) => {
    const [hours, minutes] = time.split(":");
    return `${hours}:${minutes}`;
  };

  const calculateGridPosition = (time) => {
    const [hours, minutes] = time.split(':');
    const hour = parseInt(hours, 10);
    const min = parseInt(minutes, 10);
    return hour * 60 + min;  // Переводим время в минуты
  };

  const sortedBookings = bookings.sort((a, b) => new Date(a.start_time) - new Date(b.start_time));

  return (
    <div className="timeline">
      <div className="timeline-header">
        {Array.from({ length: 9 }, (_, i) => (
          <div key={i} className="time-slot">
            {`${8 + i}:00`}
          </div>
        ))}
      </div>
      <div className="timeline-body">
        {sortedBookings.map((booking, index) => {
          const equipment = equipments.find(e => e.id === booking.equipment_id);
          const startTime = formatTime(booking.start_time.split('T')[1].slice(0, 5));
          const endTime = formatTime(booking.end_time.split('T')[1].slice(0, 5));

          // Расчёт позиции в grid
          const startPosition = calculateGridPosition(startTime);
          const endPosition = calculateGridPosition(endTime);

          return (
            <div
              key={index}
              className="booking-card"
              style={{
                gridColumnStart: startPosition / 60 + 1,  // Переводим время в столбец
                gridColumnEnd: endPosition / 60 + 1,      // Переводим время в столбец
                gridRowStart: index + 1,                   // Располагаем по строкам, если несколько бронирований
                gridRowEnd: index + 2
              }}
            >
              <h3>{equipment ? equipment.name : 'Loading...'}</h3>
              <p>{equipment ? equipment.description : 'Loading...'}</p>
              <p>{startTime} - {endTime}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Timeline;
