import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EquipmentList = () => {
    const [equipments, setEquipments] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/equipments/')
            .then(response => {
                setEquipments(response.data);
                setLoading(false);
            })
            .catch(error => {
                console.error('There was an error fetching the equipment data!', error);
                setLoading(false);
            });
    }, []);

    const handleDelete = (equipmentId) => {
        axios.delete(`http://127.0.0.1:8000/equipments/${equipmentId}/`)
            .then(response => {
                setEquipments(equipments.filter(equip => equip.id !== equipmentId));
            })
            .catch(error => {
                console.error('There was an error deleting the equipment!', error);
            });
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>Equipment List</h1>
            <div className="equipment-container">
                {equipments.map((equipment) => (
                    <div key={equipment.id} className="equipment-card">
                        <h2>{equipment.name}</h2>
                        <p>{equipment.description}</p>
                        <p>Availability: {equipment.availability_start} - {equipment.availability_end}</p>

                        {equipment.bookings && equipment.bookings.length > 0 ? (
                            <div>
                                <h3>Bookings:</h3>
                                {equipment.bookings.map((booking) => (
                                    <div key={booking.id}>
                                        <p>User: {booking.user.name}</p>
                                        <p>Start Time: {booking.start_time}</p>
                                        <p>End Time: {booking.end_time}</p>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <p>No bookings</p>
                        )}

                        <button onClick={() => handleDelete(equipment.id)}>Delete</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default EquipmentList;
